import CssBaseline from "@material-ui/core/CssBaseline";
import { ThemeProvider } from "@material-ui/core/styles";
import App from "next/app";
import Head from "next/head";
import React from "react";
import theme from "configs/theme";
import DashBoard from "@admin/Components/AdminLayout/AdminLayout";
import { AuthProvider, ProtectRoute } from "contexts/Auth.context";

class MyApp extends App {
  componentDidMount() {
    // Remove the server-side injected CSS.
    const jssStyles = document.querySelector("#jss-server-side");
    if (jssStyles) {
      jssStyles.parentElement.removeChild(jssStyles);
    }
  }

  render() {
    const { Component, pageProps, router } = this.props;
    let module;
    if (router.pathname.startsWith("/admin")) {
      let ComponentWithDashBoard = DashBoard(FFF, pageProps);
      module = (
        <ProtectRoute>
          <ComponentWithDashBoard />;
        </ProtectRoute>
      );
    } else {
      module = (
        <ThemeProvider theme={theme}>
          {/* CssBaseline kickstart an elegant, consistent, and simple baseline to build upon. */}
          <CssBaseline />
          <Component {...pageProps} />
        </ThemeProvider>
      );
    }

    return (
      <React.Fragment>
        <Head>
          <title>My page</title>
          <meta name="viewport" content="minimum-scale=1, initial-scale=1, width=device-width" />
        </Head>
        <AuthProvider>{module}</AuthProvider>
      </React.Fragment>
    );
  }
}
export default MyApp;
