import React, { Component } from "react";
import clsx from "clsx";
import { makeStyles } from "@material-ui/core/styles";
import { Drawer, CssBaseline } from "@material-ui/core";
import useIsomorphicLayoutEffect from "hooks/useLayoutEffect";

import SideBar from "modules/Admin/Components/Sidebar/Sidebar";
import Header from "modules/Admin/Components/Header/Header";
import { Dashboard } from "@material-ui/icons";

const drawerWidth = 200;
const drawerClose = 56;
const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appContent: {
    display: "flex",
    flex: "1 1 0%",
    flexDirection: "column",
  },
  drawerOpen: {
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerClose: {
    width: drawerClose,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(5),
  },
  drawerPaper: {
    width: drawerWidth,
  },
}));

function DashBoardHOC(Component, pageProps) {
  return React.memo(function DashBoard(props) {
    console.log("dashboard rerender");
    const classes = useStyles();

    const [toggle, setToggle] = React.useState(true);
    const [smallScreen, setSmallScreen] = React.useState(1000 <= 960);

    const handleDrawerToggle = () => {
      setToggle(!toggle);
    };

    useIsomorphicLayoutEffect(() => {
      function updateSize() {
        window.innerWidth <= 960 ? setSmallScreen(true) : setSmallScreen(false);
      }
      window.addEventListener("resize", updateSize);
      // updateSize()
      return () => window.removeEventListener("resize", updateSize);
    }, []);

    return (
      <div className={classes.root}>
        <CssBaseline />

        {smallScreen ? (
          <Drawer
            variant="temporary"
            open={toggle}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, // Better toggle performance on mobile.
            }}
          >
            <SideBar toggle={toggle} />
          </Drawer>
        ) : (
          <Drawer
            variant="permanent"
            className={clsx({
              [classes.drawerOpen]: toggle,
              [classes.drawerClose]: !toggle,
            })}
            classes={{
              paper: clsx({
                [classes.drawerOpen]: toggle,
                [classes.drawerClose]: !toggle,
              }),
            }}
          >
            <SideBar toggle={toggle} />
          </Drawer>
        )}

        <div className={classes.appContent}>
          <Header toggle={toggle} handleToggle={handleDrawerToggle} />
          <main className={classes.content}>
            <div className={classes.toolbar} />
            <Component {...pageProps} />
          </main>
        </div>
      </div>
    );
  });
}

export default DashBoardHOC;
